and w.device_id = #{deviceId}
and time like #{time}"%"
limit #{pageData.startPage}, #{pageData.limit}

		select count(w.id) from weathers_tb w where 1 = 1
		
			and w.device_id = #{deviceId}
		

		
			and w.time like #{time}"%"

select w.id, w.dm, w.sm, w.ta, w.ua, w.pa, w.time, d.device_name from weathers_tb w inner join device_tb d on w.device_id = d.device_id inner join company_tb c on w.company_id = c.id where 1 = 1
and w.device_id = #{deviceId}
order by time desc
